dict.string:
P=( "Default empty" "Alibaba 223.5.5.5 223.6.6.6" "V2EX 199.91.73.222 178.79.131.110" "DNSPod 114.114.114.114 114.114.115.115" "Google 8.8.8.8 8.8.4.4" "OpenDNS 42.120.21.30" )
function inet() { local net=$(netstat -rn | awk '/default/{print $NF}') echo $(networksetup -listnetworkserviceorder | \ awk "/$net/{print \$3}" | tr -d ', ') }
function item() { local s=$(echo $3 | sed 's/+/ vs /g') cat <<EOF <item uid="" arg="$4:$1"> <title>$2</title> <subtitle>$s</subtitle> <icon>icons/$4.png</icon> </item> EOF }
function cur() { local title dns s dns=$(networksetup -getdnsservers "$(inet)" | tr "\\n" " " | \ sed -e 's/ $//g') for ((i = 0; i < ${#P[@]}; i++)); do if [ ! -z $s ]; then break; fi s=$(echo ${P[$i]} | grep -iE "$dns" | awk '{print $1}') done title="Current" if [ -z $s ]; then s="Default" dns="empty" unset P[0] P=("${P[@]}") else title="$title:" fi item "$dns" "$title $s" "$dns" $s }
function def() { local S name dns ret=cur for ((i = 0; i < ${#P[@]}; i++)); do S=${P[$i]} name=$(echo $S | awk '{print $1}') dns=$(echo $S | awk '{$1=""; print $0}' | \ sed -e 's/^ //g' -e 's/ /+/g') item "$dns" $name "$dns" $name done }
echo '<?xml version="1.0"?><items>' cur def echo '</items>'
